Home_modern.global_custom_css:
body {
letter-spacing: 0;
}
.jas_title_1 {
    line-height: 40px;
font-weight: 600;
color: #000;
}
.jas-socials a, .header-text, .jas-currency, .header__top .jas-action a {
    font-size: 12px;
color: #878787;
}
.header__top {
border: none;
}
.jas-menu > li > a {
text-transform: unset;
letter-spacing: 0;
}
.jas-action i {
    font-size: 24px;
}

.metaslider .flexslider .caption-wrap h2 {
font-weight: 600;
}
 .f__libre, .metaslider .flexslider .caption-wrap h4, blockquote, q {
    font-family: unset;
    font-style: unset;
    font-size: 13px;
}
.jas-menu-label {
font-size: 9px;
}
.shopify h3.product-title, h3.product-title {
    font-weight: 500;
}
a.button-o-w {
    border-width: 2px;
    font-weight: 500;
}
.widget ul.product_list_widget li ins, .price ins {
    letter-spacing: 0;
}
.price ins {
font-weight: 400;
}
.product_delivery, .promo_shipping_text.jas_user_ship, .product_delivery_quick, .promo_shipping_text_quick, .counter_real_time, .jas_progress_bar .items-count p, .product_title, .jas_clock_ticker .block span  {
    font-weight: 400;
}

