    background-image: url(https://cdn.shopify.com/s/files/1/0014/0322/4121/files/summit.svg?5033);
    background-repeat: no-repeat;
}
.jas_instagram_section .jas_title_1 .sub-title {
position: relative;
}
.sub-title {
    font-style: unset;
}

